Bash(node -e "
const bin = require\(''@expo/ngrok-bin''\);
console.log\(''ngrok binary path:'', bin\);

const { spawn } = require\(''child_process''\);
const ngrok = spawn\(bin, [''--version''], { windowsHide: true }\);
ngrok.stdout.on\(''data'', \(d\) => console.log\(''stdout:'', d.toString\(\)\)\);
ngrok.stderr.on\(''data'', \(d\) => console.log\(''stderr:'', d.toString\(\)\)\);
ngrok.on\(''error'', \(e\) => console.log\(''error:'', e\)\);
ngrok.on\(''exit'', \(code\) => console.log\(''exit code:'', code\)\);
")
